import React, { useState, useEffect } from 'react';
import './Trending.css';
import data_product from '../Assets/data';
import Knife from '../Knife/Knife';

export const Trending = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const [randomTopPicks, setRandomTopPicks] = useState([]);

    const itemsPerPage = 36; // 36 items per page in a 4x4 grid
    const topPicksNumber = 5; // Number of top picks

    useEffect(() => {
        // Randomize top picks on component mount
        const picks = [...data_product].sort(() => 0.5 - Math.random()).slice(0, topPicksNumber);
        setRandomTopPicks(picks);
    }, []);

    // Calculate the range of items to display
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = data_product.slice(indexOfFirstItem, indexOfLastItem);

    // Function to change page
    const nextPage = () => {
        if (currentPage < Math.ceil(data_product.length / itemsPerPage)) {
            setCurrentPage(currentPage + 1);
        }
    };

    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    return (
        <div className='trending'>
            <h1>Top Picks</h1>
            <div className='trending-items'>
                {randomTopPicks.map((knife) => (
                    <Knife key={knife.id} id={knife.id} name={knife.name} image={knife.image} knife_price={knife.knife_price}/>
                ))}
            </div>

            <h1>More Items</h1>
            <div className='pagination-items'>
                {currentItems.map((item) => (
                    <Knife key={item.id} id={item.id} name={item.name} image={item.image} knife_price={item.knife_price} />
                ))}
            </div>

            {/* Pagination Controls */}
            <div className='pagination'>
                <button onClick={prevPage}>Prev</button>
                <button onClick={nextPage}>Next</button>
            </div>
        </div>
    );
}

export default Trending;
