import React, { useState, useEffect } from 'react';
import './Trending.css';
import data_product from '../Assets/data';
import Knife from '../Knife/Knife';

export const Trending = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const [randomTopPicks, setRandomTopPicks] = useState([]);

    const itemsPerPage = 36; // 36 items per page in a 4x4 grid
    const topPicksNumber = 4; // Number of top picks

    useEffect(() => {
        // Randomize top picks on component mount
        const picks = [...data_product].sort(() => 0.5 - Math.random()).slice(0, topPicksNumber);
        setRandomTopPicks(picks);
    }, []);

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = data_product.slice(indexOfFirstItem, indexOfLastItem);

    const totalPages = Math.ceil(data_product.length / itemsPerPage);

    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    const nextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    return (
        <div className='trending'>
            <h1>Top Picks</h1>
            <div className='trending-items'>
                {randomTopPicks.map((knife) => (
                    <Knife key={knife.id} id={knife.id} name={knife.name} image={knife.image}
                           knife_price={knife.knife_price}/>
                ))}
            </div>

            <h1>More Items</h1>
            <div className='pagination-items'>
                {currentItems.map((item) => (
                    <Knife key={item.id} id={item.id} name={item.name} image={item.image}
                           knife_price={item.knife_price}/>
                ))}
            </div>

            <div className='pagination'>
                <button onClick={prevPage} disabled={currentPage === 1}>Prev</button>
                <span className="page-info">{currentPage} of {totalPages}</span>
                <button onClick={nextPage} disabled={currentPage === totalPages}>Next</button>
            </div>
        </div>
    );
}

export default Trending;
