import React, { useState } from 'react';
import './Trending.css';
import data_product from '../Assets/data';
import Knife from '../Knife/Knife';

export const Trending = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 4; // 4 items per column, 10 rows means 40 items per page

    // Calculate the range of items to display
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = data_product.slice(indexOfFirstItem, indexOfLastItem);

    // Function to change page
    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    return (
        <div className='trending'>
            <h1>Top Picks</h1>
            <div className='trending-items'>
                {data_product.slice(0, 10).map((knife, i) => (
                    <Knife key={knife.id} id={knife.id} name={knife.name} image={knife.image} knife_price={knife.knife_price}/>
                ))}
            </div>

            <h1>More Items</h1>
            <div className='pagination-items'>
                {currentItems.map((item, index) => (
                    <Knife key={item.id} id={item.id} name={item.name} image={item.image} knife_price={item.knife_price} />
                ))}
            </div>

            {/* Pagination */}
            <div className='pagination'>
                {[...Array(Math.ceil(data_product.length / itemsPerPage)).keys()].map(number => (
                    <button key={number + 1} onClick={() => paginate(number + 1)}>
                        {number + 1}
                    </button>
                ))}
            </div>
        </div>
    );
}

export default Trending;
